mapper.insert:
insert into class (
     class_id
     ,class_name
     ,class_start_date
     ,class_end_date
     ,take_charge_tea
     ,class_subject
) values( 
(select (nvl(max(class_id),0)+1) from class)
   ,#{class_name}
   ,to_date(#{class_start_date},'yyyy-mm-dd')
   ,to_date(#{class_end_date},'yyyy-mm-dd')
   ,#{take_charge_tea}
   ,#{class_subject}
 )

mapper.update:
 update
    class
 set
       class_name = #{class_name}
       , class_start_date = #{class_start_date}
       , class_end_date = #{class_end_date}
       , take_charge_tea = #{take_charge_tea}
       , class_subject = #{class_subject}
where
   class_id = '${class_id}'

update
   class_join_stu
set
   stu_id = #{stu_id}
where 
   class_id = #{class_id}

mapper.select:
select
CLASS_ID "CLASS_ID" , CLASS_NAME "class_name" ,(to_char(class_start_date,'yyyy-mm-dd')) "class_start_date" ,(to_char(class_end_date,'yyyy-mm-dd')) "class_end_date" ,take_charge_tea "take_charge_tea" ,CLASS_SUBJECT "class_subject"
from class c where class_id = ${class_id}
